import React, { useState, useEffect } from 'react';
import './styles.scss';

const onClickHandler = () => {
  console.log("----------");
  console.log("Click Handler");
  console.log("----------");
}

const renderEditBook = (book, setUpdate) => {
  return <>
    <h2>{ book.title }</h2>
    <p>{ book.isbn }</p>
    <p>{ book.author }</p>
  </>
}

const renderBook = (book) => {
  return <>
    <h2>{ book.title }</h2>
    <p>{ book.isbn }</p>
    <p>{ book.author }</p>
  </>
}

function Book({ book }) {
  const [update, setUpdate] = useState(book);
  const [isEdit, setIsEdit] = useState(!book.isbn);

  return <div className='comp-book'>
    { isEdit ? renderEditBook(update, setUpdate) : renderBook(update) }

    {
      isEdit && <button className="comp-book-btn comp-book-save"
                        onClick={onClickHandler}
                        disabled={update !== book && !!update.isbn}>
                  Save
                </button>
    }

    <button className="comp-book-btn comp-book-edit"
                        onClick={() => setIsEdit(!isEdit)}>
      { isEdit ? 'Cancel' : 'Edit' }
    </button>
  </div>;
}

export default Book;
