import { getToken } from './token';

export const authAPI = async (path, data = {}, credentials = true) => {
    const authToken = getToken();

    const options = {
        method: 'POST',
        mode: 'cors',
        cache: 'no-cache',
        credentials: 'same-origin',
        headers: { 'Content-Type': 'application/json' },
        referrerPolicy: 'no-referrer',
        body: JSON.stringify(data)
    };

    if(authToken) options.headers['Authorization'] = authToken;

    return fetch('http://localhost:3001/' + path, options)
                .then(data => {
                    const res = data.json();

                    console.log("-----------");
                    console.log(res);
                    console.log("-----------");

                    if(data.status >= 500) {
                        window.alert(`There was an error:\n${res.message}`);
                    }

                    return res;
                })
                .catch(e => {
                    debugger;
                    console.log("------------");
                    console.log('e');
                    console.log(e);
                    console.log("------------");
                    window.alert('There was an error');
                });
};
