import React, { useState, useEffect } from 'react';
import TextInput from '../TextInput';
import './styles.scss';

const onSaveHandler = () => {
  console.log("----------");
  console.log("Click Handler");
  console.log("----------");
}

const updateValue = (book, key, value) => {
  return Object.assign({}, book, { [key]: value })
}

const renderEditBook = (book, setUpdate) => {
  return <>
    <TextInput text={ book.title } onChange={(value) => setUpdate(updateValue(book, 'title', value))}/>
    <TextInput text={ book.isbn } onChange={(value) => setUpdate(updateValue(book, 'isbn', value))}/>
    <TextInput text={ book.author } onChange={(value) => setUpdate(updateValue(book, 'author', value))}/>
  </>
}

const renderBook = (book) => {
  return <>
    <h2>{ book.title }</h2>
    <p>{ book.isbn }</p>
    <p>{ book.author }</p>
  </>
}

function Book({ book }) {
  const [update, setUpdate] = useState(book);
  const [isEdit, setIsEdit] = useState(!book.isbn);

  return <div className='comp-book'>
    { isEdit ? renderEditBook(update, setUpdate) : renderBook(update) }

    {
      isEdit && <button className="comp-book-btn comp-book-save"
                        onClick={onSaveHandler}
                        disabled={update !== book && !!update.isbn}>
        Save
      </button>
    }

    <button className="comp-book-btn comp-book-edit"
                        onClick={() => setIsEdit(!isEdit) && setUpdate(book)}>
      { isEdit ? 'Cancel' : 'Edit' }
    </button>
  </div>;
}

export default Book;
