import React, { useState, useEffect } from 'react';
import { listBooks } from '../../services/books';
import Book from '../Book';
import './styles.scss';

const addPlaceholder = (placeholders, setPlaceholders) => {
  placeholders.push({
    title: "",
    isbn: "",
    author: ""
  })

  setPlaceholders(placeholders);
}

function List() {
  const [updates, setUpdates] = useState(0);
  const [placeholders, setPlaceholders] = useState([]);
  const [list, setList] = useState([]);

  useEffect(() => {
    listBooks().then(setList);
  }, [updates]);

  return <div className='comp-list'>
    { 
      [...list, ...placeholders].map(book => 
        <Book key={book.isbn}
              book={book}
              onChange={() => setUpdates(updates + 1)}/>)
    }

    <button className="comp-list-add"
            onClick={() => addPlaceholder(placeholders, setPlaceholders)}>
      Add
    </button>
  </div>;
}

export default List;
