import React, { useState, useEffect } from 'react';
import Header from '../../components/Header';
import { addBook, listBooks } from '../../services/books';
import './styles.scss';

const addRandomly = (setAdd) => {
  const book = {
    name: "Random",
    isbn: "13413412",
    author: "HOLA"
  }

  addBook(book).then(() => setAdd(book));
}

function List() {
  const [add, setAdd] = useState([]);
  const [list, setList] = useState([]);

  useEffect(() => {
    listBooks().then(setList);
  }, [add])

  return (
    <div className='page-list'>
      <Header />

      <div className='page-list-content'>
        { list.map(book => {
          console.log(book);
          return <h2 key={book.title}>{book.title}</h2>
        }) }

        <div onClick={addRandomly}>
          Add
        </div>
      </div>
    </div>
  );
}

export default List;
