import React, { useState } from 'react';
import TextInput from '../TextInput';
import './styles.scss';

const onSaveHandler = () => {
  console.log("----------");
  console.log("Click Handler");
  console.log("----------");
}

const updateValue = (update, key, value) => {
  return Object.assign({}, update, { [key]: value })
}

const renderEditBook = (book, update, setUpdate) => {
  const updatedBook = Object.assign({}, book, update);

  return <>
    <TextInput text={ updatedBook.title } onChange={(value) => setUpdate(updateValue(update, 'title', value))}/>
    <TextInput text={ updatedBook.isbn } onChange={(value) => setUpdate(updateValue(update, 'isbn', value))}/>
    <TextInput text={ updatedBook.author } onChange={(value) => setUpdate(updateValue(update, 'author', value))}/>
  </>
}

const renderBook = (book) => {
  return <>
    <h2>{ book.title }</h2>
    <p>{ book.isbn }</p>
    <p>{ book.author }</p>
  </>
}

const canSaveData = (update) => {
  return Object.keys(update).length > 0 && !('isbn' in update && !update.isbn)
}

function Book({ book }) {
  const [update, setUpdate] = useState({});
  const [isEdit, setIsEdit] = useState(!book.isbn);
  const [isLoading, setIsLoading] = useState(false);

  return <div className='comp-book'>
    <div className='comp-book-wrapper'>
      { isEdit ? renderEditBook(book, update, setUpdate) : renderBook(book) }
    </div>

    {
      isEdit && <button className="comp-book-btn comp-book-save"
                        onClick={onSaveHandler}
                        disabled={!canSaveData(update)}>
        Save
      </button>
    }

    <button className="comp-book-btn comp-book-edit"
            onClick={() => {
              setIsEdit(!isEdit);
              setUpdate({});
            }}>
      { isEdit ? 'Cancel' : 'Edit' }
    </button>
  </div>;
}

export default Book;
