import React, { useState, useEffect } from 'react';
import { addBook, listBooks } from '../../services/books';
import './styles.scss';

const addRandomly = (updates, setUpdates) => {
  const book = {
    title: "Random",
    isbn: Math.random(),
    author: "HOLA"
  }

  addBook(book).then(() => setUpdates(updates + 1));
}

function List() {
  const [updates, setUpdates] = useState(0);
  const [list, setList] = useState([]);

  useEffect(() => {
    listBooks().then(setList);
  }, [updates]);

  return <div className='page-list'>
    { list.map(book => <h2 key={book.isbn}>{book.title}</h2>) }

    <div onClick={() => addRandomly(updates, setUpdates)}>
      Add
    </div>
  </div>;
}

export default List;
